import { useRef } from "react";
import { useState } from "react";


function App() {
  
  const [num1, setNum1] = useState(RandomNum());
  const [num2, setNum2] = useState(RandomNum());
  const [result, setResult] = useState('');
  const [point, setPoint] = useState(0);
  
  let RandomNum = () => Math.floor(Math.random() * 10)
 
  const refInput = useRef()
 

  function Submit(e) { 
    e.preventDefault()
    let userNum = parseInt(refInput.current.value)
    if (num1 * num2 === userNum) {
      setResult('정답')
      setNum1(RandomNum());
      setNum2(RandomNum());
      setPoint(current => current + 1)
    } else {
      setResult('오답')
      if (point >= 1) {
        setPoint(current => current - 1)
      }
    }
    refInput.current.value = '';
  }

  return (
    <>
      <h3>구구단</h3>
      <form onSubmit={Submit}>
        <input Value={num1} type="number" min="0" max="9" readOnly />
        *
        <input Value={num2} type="number" min="0" max="9" readOnly />
        =
        <input ref={refInput} type="number" min="0" max="99" />
        <button style={{ display: "none" }}>결과</button>
      </form>
      <p className="output">정답여부 : {result} / 점수 : {point}점</p>
    </>
  );
}


export default App;
