function App() {
  // const [cnt, setCnt] = useState(0);
  let n = 1
  function fn() {
    n++
    document.querySelector('.output').innerText = n
  }
  return (
    <>
      <h1>1씩증가</h1>
      <p>
        <button onClick={fn}>증가</button>
      </p>
      <p>현재값 : {n}</p>
    </>
  );
}

export default App;
