import { AppContext } from './App';



const CompBtn = () => {

const {_listArr, _setListArr} = useContext(AppContext)

  const fnAddListHandler = function () {
    const listArr = [..._listArr]
    listArr.push('')
    _setListArr(listArr)
  }
  

  return (
    <button onClick={fnAddListHandler} >
      추가하기
    </button>
  );
};

export default CompBtn;
