import { keyframes, styled } from "styled-components";

function App() {

  const Wrapper = styled.div`
 display:flex;
 align-items:center;
 justify-content:center;
 `

 
 const Emoji = styled.span`

 `

  const lotationAnimation = keyframes`
 from{
transform:rotate(0deg);
border-radius:0;
 }to{
  transform:rotate(360deg);
border-radius:100px;
 }`

  const Box = styled.div`
  display:flex;
  align-items:center;
  justify-content:center;
 width:200px;
 height:200px;
 background-color:tomato;
 animation:${lotationAnimation} 1s linear infinite;
 
 ${Emoji}{
  font-size:50px;
  &:hover{
    font-size:70px;
  }
  &:active{
    opacity:0;
  }
 }
 `
 //{/* span은 styled component가 아니지만 위와 같이 작성가능함 */}




  return (
    <>
      <Wrapper>
        <Box>
          <Emoji>😎</Emoji>
          
          {/* <span>😎</span> 여기서 span이 아니라 p를 쓰고싶다면?위에도 동일하게 변경해야함
          그러고싶지않다면 새롭게 imoji를 선언하고 바꿔줌 */}
        </Box>
      </Wrapper>
    </>
  );
}

export default App;
