import { useState } from "react";

//state와 관련이 없는 함수는 여기서 선언해주면 속도가 더 빨라질 수 있다
let fnGetrandom = () => {
  //여기서는 장점이 return을 안해도됨
  Math.floor(Math.random() * 10)
}

function App() {

  const [n1, setN1] = useState(fnGetrandom());
  const [n2, setN2] = useState(fnGetrandom());

  function fnSubmit(e) {
    e.preventDefault()
