      setN1(fnGetrandom())
      setN2(fnGetrandom())
      
    }
  }

  return (
    <>
      <h4>더하기연습</h4>
      <form onSubmit={fnSubmit}>
        <span>{n1}</span>
        +
        <span>{n2}</span>
        =
        <input type="number" min="0" max="99" />
        <button style={{ display: "none" }}>버튼</button>
      </form>
    </>
  );
}

export default App;
