import { useState } from "react";

function App() {

  const [num1, setNum1] = useState()
  const [num2, setNum2] = useState()

  function onClick() {
    setNum1(0)
    setNum2(0)
  }

  return (
    <>
      <form>
        <h1>사칙연산</h1>
        <input type="number" min="0" max="9" />
        &nbsp;
        <input type="number" min="0" max="9" />
        &nbsp;
        <button >결과보기</button>
      </form>
      <hr />
      <p>
        <b>덧셈</b>        &nbsp;
        {num1}+{num2} = {num1+num2}
      </p>
      <p>
        <b>뺄셈</b>        &nbsp;
        {num1}-{num2} ={num1-num2}
      </p>
      <p>
        <b>곱셈</b>        &nbsp;
        {num1}*{num2} ={num1*num2}
      </p>
      <p>
        <b>나눗셈</b>        &nbsp;
        {num1}/{num2} ={num1/num2}
      </p>
    </>
  );
}

export default App;


