import { useState } from "react";

let fnGetNum = () => Math.floor(Math.random() * 10)

function App() {
  const [_num1, _setNum1] = useState(fnGetNum())
  const [_num2, _setNum2] = useState(fnGetNum())

  const fnOnSubmit = function (e) {
    e.preventDefault()
    let userNum = parseInt(document.querySelector('.userNum').value)
    if(_num1+_num2 ===userNum){alert('정답')}
  }

  return (
    <>
      <h1>더하기계산</h1>
      <form onSubmit={fnOnSubmit}>
        <span>{_num1}</span>
        +
        <span>{_num2}</span>
        =

        <input className="userNum" type="number" min="0" max="20" required />
      </form>
    </>
  );
}

export default App;
